        function card(i, type) {
            const title = currentLang === 'en' ? i.en : i.ku;
            return `<div class="card" onclick="openItem('${type}', ${i.id})"><img src="${i.img}"><h4>${title}</h4><span style="color:var(--primary); font-weight:700;">${type==='products' ? i.price : (currentLang==='en'?'View':'ببینە')}</span></div>`;
        }

        function openItem(type, id) {
            const item = db[type].find(x => x.id === id);
            const title = currentLang === 'en' ? item.en : item.ku;
            const desc = currentLang === 'en' ? item.desc_en : item.desc_ku;
            const pop = document.getElementById('popup-item');
            document.getElementById('details-header').innerText = langData[currentLang].details;
            document.getElementById('popup-content-body').innerHTML = `<img src="${item.img}" style="width:100%; height:300px; object-fit:cover;"><div style="padding:30px 25px;"><h1 style="font-size:2rem; margin-bottom:15px;">${title}</h1><p style="line-height:1.8; font-size:1.1rem; opacity:0.9;">${desc}</p></div>`;
            pop.style.display = 'block';
            pop.className = 'popup fade-up';
        }

        function openEmergency() {
            const t = langData[currentLang];
            const pop = document.getElementById('popup-emergency');
            document.getElementById('e-title').innerText = t.e_title;
            document.getElementById('e-sub').innerText = t.e_sub;
            document.getElementById('e-info-title').innerText = t.e_info;
            document.getElementById('e-list').innerHTML = t.e_steps.map(s => `<li>${s}</li>`).join('');
            pop.style.display = 'block';
            pop.className = 'popup fade-up';
        }

        function openDev() {
            const pop = document.getElementById('popup-dev');
            document.getElementById('dev-bio-text').innerText = langData[currentLang].dev_bio;
            pop.style.display = 'block';
            pop.className = 'popup fade-up';
        }

        function closePopup(id) {
            const pop = document.getElementById(id);
            pop.className = 'popup fade-down-exit';
            setTimeout(() => { pop.style.display = 'none'; }, 380);
        }

        function setLang(l) {
            currentLang = l;
            document.body.setAttribute('dir', l === 'ku' ? 'rtl' : 'ltr');
            const t = langData[l];
            document.getElementById('nav-h').innerText = t.h;
            document.getElementById('nav-d').innerText = t.d;
            document.getElementById('nav-p').innerText = t.p;
            document.getElementById('nav-t').innerText = t.t;
            document.getElementById('nav-s').innerText = t.s;
            nav('settings');
        }

        function toggleTheme() {
            currentTheme = currentTheme === 'light' ? 'dark' : 'light';
            document.body.setAttribute('data-theme', currentTheme);
            nav('settings');
        }

        function calcBMI() {
            const w = document.getElementById('w').value;
            const h = document.getElementById('h').value / 100;
            const cats = langData[currentLang].bmi_cat;
            if(w && h) {
                const bmi = (w / (h * h)).toFixed(1);
                let s = (bmi < 18.5) ? cats[0] : (bmi < 25) ? cats[1] : (bmi < 30) ? cats[2] : cats[3];
                document.getElementById('bmi-result').innerHTML = `BMI: ${bmi} <br> <span style="color:var(--primary); background:white; padding:4px 12px; border-radius:10px; display:inline-block; margin-top:8px;">${s}</span>`;
            }
        }

        function doSearch(v) {
            const q = v.toLowerCase();
            const res = document.getElementById('results');
            if(!q) { render('home'); return; }
            const fd = db.diseases.filter(i => i.en.toLowerCase().includes(q) || i.ku.includes(q));
            if(!fd.length) res.innerHTML = `<div style="text-align:center; padding:50px;">${langData[currentLang].notfound}</div>`;
            else res.innerHTML = `<div class="grid" style="margin-top:20px;">${fd.map(i=>card(i,'diseases')).join('')}</div>`;
        }
    